import{_ as t,c as r,aa as a,o as i}from"./chunks/framework.bT2BobGK.js";const m=JSON.parse('{"title":"勤劳致富","description":"","frontmatter":{"breadcrumb":false,"navbar":true,"sidebar":true,"pageInfo":true,"contributor":true,"editLink":true,"updateTime":true,"prev":true,"next":true,"comment":true,"time":"2022-05-24T00:00:00.000Z","footer":true,"backtotop":true,"title":"勤劳致富","sticky":true,"word":true,"category":"牛马人生","star":true,"original":true},"headers":[],"relativePath":"design/勤劳不能致富.md","filePath":"design/勤劳不能致富.md"}'),o={name:"design/勤劳不能致富.md"};function n(l,e,s,u,d,c){return i(),r("div",null,e[0]||(e[0]=[a('<h1 id="一个扎心的话题" tabindex="-1">一个扎心的话题 <a class="header-anchor" href="#一个扎心的话题" aria-label="Permalink to &quot;一个扎心的话题&quot;">​</a></h1><p>勤劳为什么不能致富？</p><ul><li>如果拜佛有用，你连门都进不去；</li><li>如果有长生不老的药，马云都没资格买；</li><li>如果种地可以赚钱，农民将无地可种；</li><li>如果大便能被资本控制，你将失去屁股的使用权和控制权。</li><li>如果勤劳能致富，你挑个大粪也在送礼走关系</li></ul><h2 id="一个段子" tabindex="-1">一个段子 <a class="header-anchor" href="#一个段子" aria-label="Permalink to &quot;一个段子&quot;">​</a></h2><p>主人对拉磨的驴说：“你真的很了不起，天天吃得少，吃的粗粮，干得多，真的很佩服你。”然后驴憨厚地笑了，拉磨的步伐更快了。 晚上，主人一家吃着驴拉磨磨出的粮，一边教育小孩说：“千万不要学那头蠢驴，它这一辈子就知道围绕着磨盘转。” 然后驴呢，接受了一天的劳累，一边吃着玉米杆，一边对小驴说：“你要快快长大，长大我教你拉磨，拉磨是一个受人尊敬的职业。”</p><p>洛克菲勒曾经说过：“整天工作的人是发不了财的，财富是对认知的补偿，而不是对勤奋的奖赏。”是不是觉得这话有点颠覆认知？ 就像网上那个段子说的，如果大便能被资本控制，你将失去屁股的使用权和控制权。还有，如果种地可以赚钱，农民将无地可种；如果拜佛有用，你连门都进不去。听起来很荒诞，但细想又很现实。 咱们很多人一直坚信勤劳能致富，可当贫穷、劳累、勤奋到了一定程度，真的就没有能力去思考未来和眼界了。就像拉磨的驴，以为拉磨是个受人尊敬的职业，却不知道自己只是被利用。 王硕都说，<strong>用穷人的艰辛和苦难当做励志故事来愚弄底层，是最阴险最歹毒的正能量。</strong> 咱们可不能被这种观念给忽悠了！所以，别再盲目相信勤劳一定能致富啦，咱们得提升认知，找到正确的致富之路！</p>',6)]))}const _=t(o,[["render",n]]);export{m as __pageData,_ as default};
