import{_ as l,o as e,c as i,a4 as r}from"./chunks/framework.B8fosacB.js";const u=JSON.parse('{"title":"发票管理","description":"","frontmatter":{"title":"发票管理","editLink":true,"navbar":true,"category":"06-财务结算"},"headers":[],"relativePath":"post/scm/finance/发票管理.md","filePath":"post/scm/finance/发票管理.md","lastUpdated":1731158524000}'),t={name:"post/scm/finance/发票管理.md"};function o(n,a,d,s,h,c){return e(),i("div",{"data-pagefind-body":!0},a[0]||(a[0]=[r('<h1 id="发票管理" tabindex="-1">发票管理 <a class="header-anchor" href="#发票管理" aria-label="Permalink to &quot;发票管理&quot;">​</a></h1><p><a href="https://inv-veri.chinatax.gov.cn/fpcs/fpzl.html" target="_blank" rel="noreferrer">国家税务总局全国增值税发票查验平台</a><a href="https://inv-veri.chinatax.gov.cn/fpcs/fpbs.html" target="_blank" rel="noreferrer">发票版式</a></p><h2 id="增值税发票主要包括以下五个票种" tabindex="-1">增值税发票主要包括以下五个票种 <a class="header-anchor" href="#增值税发票主要包括以下五个票种" aria-label="Permalink to &quot;增值税发票主要包括以下五个票种&quot;">​</a></h2><p>发票是单位和个人在购销商品、提供或者接受服务以及其他经营活动中，开具、取得的收付款凭证。</p><ol><li>数电票 <ul><li>全面数字化的发票，是与纸质发票具有同等法律效力的全新发票，不以纸质形式存在、不用介质支撑、不需申请领用。电子发票将纸质发票的票面信息全面数字化，通过标签管理将多个票种集成归并为电子发票单一票种，设立税务数字账户，实现全国统一赋码、智能赋予发票开具金额总额度、自动流转交付。</li></ul></li><li>增值税专用发票(含增值税电子专用发票) <ul><li>增值税纳税人销售货物或者提供应税劳务开具的发票，是购买方支付增值税额并可按照增值税有关规定据 <span style="color:#e64b7d;font-weight:bold;background-color:var(--vp-code-bg);border-radius:4px;padding:3px 6px;">以抵扣增值税进项税额的凭证</span>。</li></ul></li><li>增值税普通发票(含电子普通发票、卷式发票、通行费发票) <ul><li>是增值税纳税人销售货物或者提供应税劳务、服务时，通过增值税税控系统开具的普通发票。</li></ul></li><li>机动车销售统一发票 <ul><li>凡从事机动车零售业务的单位和个人，从2006年8月1日起，在销售机动车（不包括销售旧机动车）收取款项时开具的发票。</li></ul></li><li>二手车销售统一发票 <ul><li>二手车经销企业、经纪机构和拍卖企业，在销售、中介和拍卖二手车收取款项时，通过开票软件开具的发票。</li></ul></li></ol><h2 id="增值税专用发票与普通发票的主要区别" tabindex="-1">增值税专用发票与普通发票的主要区别? <a class="header-anchor" href="#增值税专用发票与普通发票的主要区别" aria-label="Permalink to &quot;增值税专用发票与普通发票的主要区别?&quot;">​</a></h2><p><strong>来自国家税务总局的介绍:</strong></p><ul><li>增值税专用发票不仅是购销双方收付款的凭证，而且还可以用作购买方（增值税一般纳税人）扣除增值税的凭证，因此不仅具有商事凭证的作用，而且具备完税凭证的作用。</li><li>而增值税普通发票除税法规定的经营项目外都不能抵扣进项税。</li></ul><h2 id="红票和蓝票" tabindex="-1">红票和蓝票 <a class="header-anchor" href="#红票和蓝票" aria-label="Permalink to &quot;红票和蓝票&quot;">​</a></h2><p>红字发票表示负数，蓝字发票就是正常销售所开具的正数发票。蓝票是货物售出后开具给购买方的发票，开出蓝票后并收取与发票一致的货款。</p><p><span style="color:#e64b7d;font-weight:bold;background-color:var(--vp-code-bg);border-radius:4px;padding:3px 6px;">什么情况下要开具红字发票</span></p><ol><li>即符合作废条件的，发票注明作废，开票软件注明作废。 收到退回的发票联、抵扣联时间未超过销售方开票当月; 销售方未抄税并且未记账;购买方未认证或者认证结果为“纳税人识别号认证不符”、“专用发票代码、号码认证不符”。</li><li>不符合作废条件：购货方已认证由购货方填写申请单，如 认证通过可以向主管税务机关填写申请,税务机关返回通知单(一式三联，一联购货方留方，一联由购货方给销货方，一联税务留存)。</li></ol><h3 id="代表不同" tabindex="-1">代表不同 <a class="header-anchor" href="#代表不同" aria-label="Permalink to &quot;代表不同&quot;">​</a></h3><ol><li>红字发票：红字发票代表的是冲抵的，是负数。</li><li>蓝字发票：蓝字发票代表的是收入的，是正数。</li></ol><h3 id="性质不同" tabindex="-1">性质不同 <a class="header-anchor" href="#性质不同" aria-label="Permalink to &quot;性质不同&quot;">​</a></h3><ol><li>红字发票：红字发票是销项负数发票。</li><li>蓝字发票：蓝字发票是一般情况下开具的发票。</li></ol><h3 id="目的不同" tabindex="-1">目的不同 <a class="header-anchor" href="#目的不同" aria-label="Permalink to &quot;目的不同&quot;">​</a></h3><ol><li>红字发票：红字发票的目的为冲抵蓝字发票的金额，那么最终某项经济活动的最终金额就是蓝字发票的金额减去红字发票的金额。</li><li>蓝字发票：蓝字发票的目的为缴纳税费。</li></ol><h2 id="一式三联" tabindex="-1">一式三联 <a class="header-anchor" href="#一式三联" aria-label="Permalink to &quot;一式三联&quot;">​</a></h2><ol><li>第一联为记账联，由销售方保留作为核算销售收入和增值税销项税额的记账凭证。</li><li>第二联为抵扣联，由购买方用于报送主管税务机关进行认证，作为抵扣增值税进项税额的凭证。</li><li>第三联为发票联，由购买方保留作为核算采购成本和增值税进项税额的记账凭证。</li></ol>',20)]))}const b=l(t,[["render",o]]);export{u as __pageData,b as default};
