import{_ as d,o as r,c as a,a4 as e}from"./chunks/framework.B8fosacB.js";const f=JSON.parse('{"title":"科目速记","description":"","frontmatter":{"title":"科目速记","editLink":true,"navbar":true,"category":"06-财务结算"},"headers":[],"relativePath":"post/scm/finance/科目速记.md","filePath":"post/scm/finance/科目速记.md","lastUpdated":1731158524000}'),o={name:"post/scm/finance/科目速记.md"};function n(i,t,s,c,l,h){return r(),a("div",{"data-pagefind-body":!0},t[0]||(t[0]=[e('<h1 id="科目速记" tabindex="-1">科目速记 <a class="header-anchor" href="#科目速记" aria-label="Permalink to &quot;科目速记&quot;">​</a></h1><p>在做企业费用管理的时候经常要记科目号，多少需要了解下。不然会计科目都看不懂，更不要说记账是否正确了。 但是这个东西, 就是工具。没必要死记硬背。知道怎么查就行了。能记多少是多少就行。</p><h2 id="常见科目速记" tabindex="-1">常见科目速记 <a class="header-anchor" href="#常见科目速记" aria-label="Permalink to &quot;常见科目速记&quot;">​</a></h2><table><thead><tr><th>科目名称</th><th>解释</th></tr></thead><tbody><tr><td>库存现金</td><td>我抽屉里有 100块</td></tr><tr><td>银行存款</td><td>我银行卡就 100块</td></tr><tr><td>其他货币资金</td><td>我手机卡有 50块</td></tr><tr><td>应收账款</td><td>小李买我的书，还没付钱</td></tr><tr><td>预付账款</td><td>双12, 我提前付定金的钱。</td></tr><tr><td>交易性金融资产</td><td>我买了一些股票和基金，想赚点儿小钱</td></tr><tr><td>长期股权投资</td><td>他们董事会有我的人</td></tr><tr><td>应收股利</td><td>我买的股票，要给我分红啦</td></tr><tr><td>应收利息</td><td>我把钱借给小张收利息</td></tr><tr><td>坏账准备</td><td>我借小张100块，预计他只能还我99，差1块就是我的坏账</td></tr><tr><td>工程物资</td><td>命我准备盖大房子，买的钢筋、水泥和砂石等</td></tr><tr><td>在建工程</td><td>命大房子施工中...</td></tr><tr><td>固定资产</td><td>奋大房子建好啦...我带闺蜜一起搬进去住</td></tr><tr><td>累计折旧</td><td>偷大房子住了好几年，有点旧了</td></tr><tr><td>固定资产减值准备</td><td>我的大房子风格旧了，不时髦了...跌价了</td></tr><tr><td>长期待摊费用</td><td>把我那不时髦的大房子重新装修一下，花的钱未来一起摊</td></tr><tr><td>固定资产清理</td><td>准备把我的大房子卖掉置换，放这里理一理，回头汇总看看赚了还是赔了</td></tr><tr><td>无形资产</td><td>花98块买了个爱奇艺年卡追爱豆新剧</td></tr><tr><td>累计摊销</td><td>会员费每个月摊8块1毛6</td></tr><tr><td>原材料</td><td>煮珍珠奶茶的珍珠</td></tr><tr><td>在途物资</td><td>我买的珍珠还在路上呢</td></tr><tr><td>库存商品</td><td>加工好的奶茶等你来买</td></tr><tr><td>递延所得税资产</td><td>今年多交的税，未来可以抵</td></tr><tr><td>短期借款</td><td>明年我就要还银行的钱</td></tr><tr><td>长期借款</td><td>这笔钱不着急还银行</td></tr><tr><td>未确认融资费用</td><td>未来要一起摊的利息</td></tr><tr><td>应付账款</td><td>今天买了1支笔，还没付</td></tr><tr><td>预收账款</td><td>我的书要预定，收定金的</td></tr><tr><td>应付职工薪酬</td><td>下个月15号要给员工发的工资</td></tr><tr><td>应交税费</td><td>欠税局的钱</td></tr><tr><td>应付股利</td><td>欠股东的股利</td></tr><tr><td>应付利息</td><td>欠债主的利息</td></tr><tr><td>递延所得税负债</td><td>现在少交、未来要补上的税</td></tr><tr><td>股本</td><td>公司发行的股票，多少股记多少钱（我们国家法定的1块钱1股）</td></tr><tr><td>资本公积-股本溢价</td><td>我们公司股票1股50块，1块钱在上面科目，剩下49块记这里</td></tr><tr><td>库存股</td><td>公司买回来的自己家的股票准备奖励给高管（是我嘛）</td></tr><tr><td>本年利润</td><td>辛苦一年的成果</td></tr><tr><td>利润分配</td><td>利润的去向，以及每年剩余留在企业的利润</td></tr><tr><td>盈余公积</td><td>每年赚的钱要留一部分下来以防万一（法定比例10%）</td></tr><tr><td>生产成本</td><td>煮珍珠奶茶用的珍珠、牛奶、茶、人工等</td></tr><tr><td>制造费用</td><td>奶茶店的水电费等</td></tr><tr><td>研发支出</td><td>搞研发投入的钱</td></tr><tr><td>合同取得成本</td><td>销售又签了大单，要给他的提成</td></tr><tr><td>合同履约成本</td><td>客户需要我们给他建一栋大房子，我们花在建房子上的钱</td></tr><tr><td>主营业务收入</td><td>主业赚的钱</td></tr><tr><td>其他业务收入</td><td>搞副业赚的钱</td></tr><tr><td>公允价值变动损益</td><td>昨天买的基金，今天涨价了耶</td></tr></tbody></table><h2 id="常用会计科目借贷秘籍" tabindex="-1">常用会计科目借贷秘籍 <a class="header-anchor" href="#常用会计科目借贷秘籍" aria-label="Permalink to &quot;常用会计科目借贷秘籍&quot;">​</a></h2><p><img src="https://img.springlearn.cn/blog/3a0aaded368f0f78d10c0de2e8788a56.png" alt=""></p>',6)]))}const m=d(o,[["render",n]]);export{f as __pageData,m as default};
